function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    //Type appropriate comment here, and begin script below
    var temp = g_form.getValue('priority', 5);

    g_form.clearOptions('tired_of_snow');
    var opt = g_form.getOption('u_test_field');
    if (newValue == 'Select') {
        g_form.addOption('tired_of_snow', 'Yes', 'Yes');
        g_form.addOption('tired_of_snow', 'Test', 'This is Joke');
    } else {
        g_form.addOption('tired_of_snow', 'No', 'No');
        g_form.addOption('tired_of_snow', 'Test', 'This is Joke');

    }
}
